import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ onLogin, setShowRegister }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      onLogin();
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  return (
    <div className="min-h-screen bg-black flex items-center justify-center">
      <div className="bg-black/80 p-16 rounded">
        <h1 className="text-white text-4xl font-bold mb-8">Sign In</h1>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full bg-gray-700 text-white p-4 rounded"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full bg-gray-700 text-white p-4 rounded"
            required
          />
          <button type="submit" className="w-full bg-red-600 text-white p-4 rounded font-bold hover:bg-red-700">
            Sign In
          </button>
        </form>
        <p className="text-gray-400 mt-4">
          New to Netflix?{' '}
          <span onClick={() => setShowRegister(true)} className="text-white cursor-pointer hover:underline">
            Sign up now
          </span>
        </p>
      </div>
    </div>
  );
};

export default Login;
